#include <iostream>#include <vector> using namespace std; int main(){    int m, n, a = 0;     cin >> n;    cin >> m;     int min = n;    if (m < n)    {        min = m;    }     for (int i = 0; i <= min; i++)    {        for (int j = 0; j <= min; j++)        {            if (((i * i) + j == m) && ((j * j) + i == n))            {                a++;            }        }    }    cout << a;     return 0;}