#include <iostream>#include <vector>#include <algorithm>using namespace std; int main(){    int t;     cin >> t;     while (t--)    {        int n, a, Cmax = 0;        vector<int> arr;        cin >> n;         for (int i = 0; i < n; i++)        {            cin >> a;            arr.push_back(a);            if (a > Cmax)            {                Cmax = a;            }        }        if (Cmax > arr[0])        {            auto it = find(arr.begin(), arr.end(), Cmax);            swap(arr[0], *it);        }        int sum = arr[0];        int Rmax = arr[0];        for (int i = 1; i < n; i++)        {            if (arr[i] > Rmax)            {                Rmax = arr[i];            }             sum += Rmax;        }         cout << sum << "\n";    }     return 0;}