#include <iostream>#include <vector> using namespace std; int main(){     int n;    int count = 0;    cin >> n;     while (n != 0)    {        vector<int> inp(3);        int c = 0;         for (auto inps : inp)        {            cin >> inps;            if (inps == 1)            {                c++;            }        }        if (c >= 2)        {            count++;        }         n--;    }     cout << count;     return 0;}